mapper.select:
SELECT id, btype, title, writer, context, createDate, updateDate, viewCnt
  FROM BOARD_T
 WHERE id = #{id}

mapper.update:
UPDATE BOARD_T
   SET viewCnt = viewCnt + 1
 WHERE id = #{id}

UPDATE BOARD_T
   SET title = #{title}
     , context = #{context}
     , updateDate = SYSDATE
 WHERE id = #{id}
